$(document).ready(function(){"use strict";$("#add_line_item_variant_id").change(function(){var t=$(this).val(),a=_.find(window.variants,function(a){return a.id==t});$("#stock_details").html(variantLineItemTemplate({variant:a})),$("#stock_details").show(),$("button.add_variant").click(addVariant),$(".with-tip").powerTip({smartPlacement:!0,fadeInTime:50,fadeOutTime:50,intentPollInterval:300})})}),addVariant=function(){$("#stock_details").hide();var t=$("input.variant_autocomplete").val(),a=$("input.quantity[data-variant-id='"+t+"']").val();return adjustLineItems(order_number,t,a),1},adjustLineItems=function(t,a,i){var e=Spree.routes.orders_api+"/"+t+"/line_items";$.ajax({type:"POST",url:Spree.url(e),data:{line_item:{variant_id:a,quantity:i}}}).done(function(){window.Spree.advanceOrder(),window.location.reload()})};